package adventofcode;

import static org.junit.Assert.*;

import org.junit.Test;

/**
 * BathroomSecurityTest
 *
 * @author naheon
 * @version 2.0.0
 * @since 2016. 12. 04.
 */
public class BathroomSecurityTest {
	BathroomSecurity security;

	String[] input1 = new String[]{"ULL", "RRDDD", "LURDL", "UUUUD"};
	String[] input2 =
		{"",
			"",
			"",
			"",
			""};

	@Test
	public void test1() throws Exception {
		security = new BathroomSecurity(input1);
		assertEquals("1985", security.numericPassword());
	}

	@Test
	public void test2() throws Exception {
		security = new BathroomSecurity(input2);
		assertEquals("61529", security.numericPassword());
	}

	@Test
	public void test3() throws Exception {
		security = new BathroomSecurity(input1);
		assertEquals("5DB3", security.alphaNumericPassword());
	}

	@Test
	public void test4() throws Exception {
		security = new BathroomSecurity(input2);
		assertEquals("C2C28", security.alphaNumericPassword());
	}
}